import React from "react";
import { Redirect } from "react-router-dom";
import Ecom from "../Ecom.js";
import CreateProduct from "./CreateProduct.js";
import ManageProducts from "./ManageProducts.js";
import Product from "./Product.js";
import Sidebar from "./Sidebar.js";


export default function Home() {

    let isLoggedIn = Ecom.getCookie("session-member-record");
    let cookie = JSON.parse(Ecom.getCookie("session-member-record"));
    var url = window.location.href;
    url = url.split('/')[3]
    if (isLoggedIn && cookie.user.user_type === 3) {

        return (
            <>
                <Sidebar />

                <div className="main">
                    {(() => {
                        if (url === "admin") {
                            return (
                                <Product />
                            )
                        }
                        if (url === "create-product") {
                            return (
                                <CreateProduct />
                            )
                        } if (url === "manage-products") {
                            return (
                                <ManageProducts />
                            )
                        }
                    })()}


                </div>
            </>
        );
    } else {
        return (
            <Redirect to="/" />
        )
    }
}
