import React, { useEffect, useState } from "react";
import Pagination from 'react-js-pagination'
import Ecom from "../Ecom.js";

export default function Product(pageNumber) {

  const [products, setProducts] = useState([]);
  const [links, setLinks] = useState([]);
  const dataLoad = async () => {
    let token = JSON.parse(Ecom.getCookie("session-member-record"));
    const req = await fetch(
      "http://127.0.0.1:8000/api/product/select-products?page=1",
      {
        headers: new Headers({
          Authorization: "Bearer " + token["access_token"],
        }),
      }
    );
    let  res = await req.json();
    let data = res["ret_data"].data;
    setLinks(res["ret_data"].next_page_url)
    setProducts(data);
  };

  useEffect(() => {
    dataLoad();
  }, []);
  return (
    <>
      <div className="container">
        <div className="row">
          {products.map((item, key) => {
            return (
              <div className="col-md-4" key={key}>
                <div className="card p-2">
                  <img
                    src={"http://127.0.0.1:8000/"+item['image_path']}
                    className="card-img-top"
                    alt="..."
                  />
                  <div className="card-body">
                    <p className="card-text">
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </p>
                  </div>
                </div>
              </div>
            );
          })}

        </div>
        <a href={links}>Next</a>
      </div>
    </>
  );
}
