import React, { useEffect, useState } from "react";
import Ecom from "../Ecom.js";
import UpdateProduct from "./UpdateProduct";
import swal from 'sweetalert';

export default function ManageProducts() {
    const [products, setProducts] = useState([]);
    const dataLoad = async () => {
        let token = JSON.parse(Ecom.getCookie("session-member-record"));
        const req = await fetch(
            "http://127.0.0.1:8000/api/product/select-products",
            {
                headers: new Headers({
                    Authorization: "Bearer " + token["access_token"],
                }),
            }
        );
        let data = await req.json();
        data = data["ret_data"];

        setProducts(data);
    };

    useEffect(() => {
        dataLoad();
    }, []);


    return (
        <div className="container mt-5">
            <div className="rounded shadow p-5">
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Product Name</th>
                            <th scope="col">Price</th>
                            <th scope="col">Content</th>
                            <th scope="col">Image</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>

                    <tbody>
                        {products.map((product, key) => {
                            return (
                                <tr key={key}>
                                    <th scope="row">{key}</th>
                                    <th >{product['name']}</th>
                                    <td>{product['price']}</td>
                                    <td>{product['content']}</td>
                                    <td>                   <img
                                        src={"http://127.0.0.1:8000/" + product['image_path']} style={{ height: 70 }}
                                        className="card-img-top"
                                        alt="..."
                                    /></td>
                                    <td className="d-flex">
                                        <button type="button" className="btn btn-primary mx-2" data-bs-toggle="modal" data-bs-target="#staticBackdrop">
                                           Edit
                                        </button>

                                        <button className="btn btn-warning" type="button" onClick={deleteAction(product['id'])}>
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
            <UpdateProduct />
        </div>

    );

}
