import React from "react";
import Ecom from "../Ecom.js";

export default function Product() {
  let data = "";
  const dataLoad = async () => {
    let token = JSON.parse(Ecom.getCookie("session-member-record"));
    // console.log(token["access_token"]);
    const res = await fetch(
      "http://127.0.0.1:8000/api/product/select-products",
      {
        headers: new Headers({
          Authorization: "Bearer " + token["access_token"],
        }),
      }
    );
    data = await res.json();
    data = data['ret_data'];
    console.log(data);
  };
  dataLoad();
  return (
    <>
      <div className="container">
        <div className="row">
          {data.forEach((item) => {
            return (
              <div className="col-md-4">
                <div class="card">
                  <img
                    src="https://thermaltake.azureedge.net/pub/media/catalog/product/cache/6bf0ed99c663954fafc930039201ed07/l/2/l20m01.jpg"
                    class="card-img-top"
                    alt="..."
                  />
                  <div class="card-body">
                    <p class="card-text">
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </p>
                  </div>
                </div>
              </div>
            );
          })}

          <div className="col-md-4">
            <div class="card">
              <img
                src="https://thermaltake.azureedge.net/pub/media/catalog/product/cache/6bf0ed99c663954fafc930039201ed07/l/2/l20m01.jpg"
                class="card-img-top"
                alt="..."
              />
              <div class="card-body">
                <p class="card-text">
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </p>
              </div>
            </div>
          </div>
          <div className="col-md-4">
            <div class="card">
              <img
                src="https://thermaltake.azureedge.net/pub/media/catalog/product/cache/6bf0ed99c663954fafc930039201ed07/l/2/l20m01.jpg"
                class="card-img-top"
                alt="..."
              />
              <div class="card-body">
                <p class="card-text">
                  Some quick example text to build on the card title and make up
                  the bulk of the card's content.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
